*/


import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "base64-sol/base64.sol";
import "./lib/ColorGenerator.sol";
import "./NBADescriptor.sol";
import "./interfaces/IERC6551Registry.sol";
/**
 * @title NBA NFT Contract
 * @author Danny Thomx
 * @notice This contract mints and manages the NBA NFTs.
 * @dev Ensure you're familiar with ERC721 standard before interacting with this contract.
 */

contract NBA is ERC721Enumerable, Ownable {
    using Strings for uint256;
    using Strings for uint160;
    using Counters for Counters.Counter;

    /// @dev Maximum number of NFTs available for minting.
    uint256 public maxSupply = 2000;

    /// @dev Cost of minting an NFT.
    uint256 public price = 0.02 ether;

    /// @dev Maximum number of NFTs one can mint in a single transaction.
    uint256 public maxMintAmount = 5;

    /// @dev State variable to control minting functionality.
    bool public paused = false;

    /// @dev Address of the team for revenue sharing.
    address public team = 0xCA7632327567796e51920F6b16373e92c7823854;

    /// @dev Address of the buidlGuidl for revenue sharing.
    address public buidlGuidl = 0x97843608a00e2bbc75ab0C1911387E002565DEDE;

    /// @dev 20% in basis points
    uint256 private constant BUIDLGUIDL_ALLOCATION_BASIS_POINTS = 2000;

    /// @dev Represents 100%
    uint256 private constant BASIS_POINTS = 10000;

    /// @dev Address of the default account implementation.
    address public defaultAccountImplementation;

    /// @dev Counter for tracking tokenIds.
    Counters.Counter private _tokenIdCounter;

    /// @dev Mapping to store colors associated with NFTs.
    mapping(uint256 => bytes3) public color_1;
    mapping(uint256 => bytes3) public color_2;
    mapping(uint256 => bytes3) public color_3;
    mapping(uint256 => bytes3) public color_4;

    /// @dev Mapping to store default accounts for each tokenId.
    mapping(uint256 => address) public tokenIdToDefaultAccountImplementation;

    /// @dev Mapping to store main accounts for each tokenId.
    mapping (uint256 => address) public mainAccount;
    address private immutable tokenDescriptor;
    IERC6551Registry public registry;
    /**
     * @notice Contract constructor to set the name and symbol for the NFT.
     * @param _name Name of the NFT.
     * @param _symbol Symbol of the NFT.
     */
    constructor(
        string memory _name,
        string memory _symbol,
        address _descriptor,
        IERC6551Registry _registry,
        address _defaultImplementation
    ) ERC721(_name, _symbol) {
        tokenDescriptor = _descriptor;
        defaultAccountImplementation =_defaultImplementation;
        registry = _registry;
      }

    /// @notice Allow the contract to receive Ether.
    receive() external payable {}

    /**
     * @notice Owner users to change team's address.
     * @param _newAddress new team address.
     */
    function setTeamAddress(address _newAddress) public onlyOwner {
        team = _newAddress;
    }

    /**
     * @notice Owner users to change buidlGuidl's address.
     * @param _newAddress new buidlGuidl address.
     */
    function setBGAddress(address _newAddress) public onlyOwner {
        buidlGuidl = _newAddress;
    }

    /**
     * @notice Owner users to pause minting of NFTs.
     * @param _isPaused new pause state.
     */
    function pause(bool _isPaused)public onlyOwner {
        paused = _isPaused;
    }

    /**
     * @notice Allows users to mint new NFTs.
     * @param _mintAmount Number of NFTs user wants to mint.
     * @dev The function checks for maximum minting limit and ensures correct payment.
     */
    function mintItem(uint256 _mintAmount) public payable {
        uint256 supply = totalSupply();
        require(!paused, "Minting paused");
        require(supply < maxSupply, "Minting over");
        require(_mintAmount > 0,"Zero amount");
        require(_mintAmount <= maxMintAmount, "Exceeds max mint allowed");

        if (msg.sender != owner()) {
            require(msg.value >= price * _mintAmount, "Insufficient ETH");
        }

        for (uint256 i = 1; i <= _mintAmount; i++) {
            _tokenIdCounter.increment();
            uint256 _tokenId = _tokenIdCounter.current();
            _safeMint(msg.sender, _tokenId);
            ColorGenerator.generateColors(_tokenId, color_1, color_2, color_3, color_4);
            address _createdAccount = registry.createAccount(defaultAccountImplementation, block.chainid, address(this), _tokenId, _tokenId, "");
            require(_createdAccount != address(0), "Failed to create account");
            tokenIdToDefaultAccountImplementation[_tokenId] = defaultAccountImplementation;
            setMainAccount(_tokenId,_createdAccount);
        }
    }

    /**
     * @notice Set main account for a specific tokenId.
     * @param tokenId The ID of the NFT.
     * @param _accountAddress Address of the main account.
     */
    function setMainAccount(uint256 tokenId, address _accountAddress) public {
        require(msg.sender == ownerOf(tokenId), "Not NFT Owner");
        mainAccount[tokenId] = _accountAddress;
    }

    /**
     * @notice Set default account implementation.
     * @param _accountAddress Address of the default account implementation.
     * @dev Only the owner can set this.
     */
    function setDefaultAccountImplementation( address _accountAddress) public onlyOwner {
        defaultAccountImplementation = _accountAddress;
    }

    /**
     * @notice Provides the URI for a specific NFT.
     * @param id The ID of the NFT.
     * @return _tokenURI URI of the NFT.
     */
    function tokenURI(uint256 id) public view override returns (string memory _tokenURI) {
        require(_exists(id), "not exist");
        NFTDescriptor.SVGParams memory _svgParams = NFTDescriptor.SVGParams({
            chainId: block.chainid,
            tokenId: id,
            color_1: color_1[id],
            color_2: color_2[id],
            color_3: color_3[id],
            color_4: color_4[id],
            implementation: tokenIdToDefaultAccountImplementation[id],
            owner: ownerOf(id),
            mainAccount: mainAccount[id]
        });

        _tokenURI = NBADescriptor(tokenDescriptor).constructTokenURI(_svgParams);
    }

    /**
     * @notice Sets the maximum amount of NFTs that can be minted in one transaction.
     * @param _newMaxSupply New max mint amount.
     * @dev Only the owner can change this.
     */
    function setMaxSupply(uint256 _newMaxSupply) public onlyOwner {
        maxSupply = _newMaxSupply;
    }

    /**
     * @notice Allows the owner to withdraw the contract's balance.
     * @dev Only the owner can call this.
     */
    function withdraw() public onlyOwner {
        uint256 balance = address(this).balance;
        uint256 buidlGuidlShare = balance * BUIDLGUIDL_ALLOCATION_BASIS_POINTS / BASIS_POINTS;
        payable(team).transfer(balance - buidlGuidlShare);
        payable(buidlGuidl).transfer(buidlGuidlShare);
    }

}","keccak256":"0xce309fa615194728162c76abfa16bae13291005a5ed7c1e0b13005fa7bbfe7e8","license":"MIT"},"contracts/NBANFT/interfaces/IERC6551Registry.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

interface IERC6551Registry {
    /**
     * @dev The registry SHALL emit the AccountCreated event upon successful account creation
     */
    event AccountCreated(
        address account,
        address indexed implementation,
        uint256 chainId,
        address indexed tokenContract,
        uint256 indexed tokenId,
        uint256 salt
    );

    /**
     * @dev Creates a token bound account for a non-fungible token
     *
     * If account has already been created, returns the account address without calling create2
     *
     * If initData is not empty and account has not yet been created, calls account with
     * provided initData after creation
     *
     * Emits AccountCreated event
     *
     * @return the address of the account
     */
    function createAccount(
        address implementation,
        uint256 chainId,
        address tokenContract,
        uint256 tokenId,
        uint256 seed,
        bytes calldata initData
    ) external returns (address);

    /**
     * @dev Returns the computed token bound account address for a non-fungible token
     *
     * @return The computed address of the token bound account
     */
    function account(
        address implementation,
        uint256 chainId,
        address tokenContract,
        uint256 tokenId,
        uint256 salt
    ) external view returns (address);
}","keccak256":"0xd6fb4bfa07d81b5d93a9f7333ca506c56d1c76a01da5201360369e2aaf957f23","license":"MIT"},"contracts/NBANFT/lib/ColorGenerator.sol":{"content":"//SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

/// @title ColorGenerator
/// @notice This library is used to generate colors for NBA NFTs.
/// @dev The generated colors are based on predictable randomness.
library ColorGenerator {

    /**
     * @notice Generates colors for a given tokenId and stores them in the provided mappings.
     * @param _tokenId The tokenId for which the colors will be generated.
     * @param color_1 Mapping to store the first color.
     * @param color_2 Mapping to store the second color.
     * @param color_3 Mapping to store the third color.
     * @param color_4 Mapping to store the fourth color.
     */
    function generateColors(
        uint256 _tokenId,
        mapping(uint256 => bytes3) storage color_1,
        mapping(uint256 => bytes3) storage color_2,
        mapping(uint256 => bytes3) storage color_3,
        mapping(uint256 => bytes3) storage color_4
    ) internal {
        bytes32 predictableRandom = keccak256(
            abi.encodePacked(
                _tokenId,
                blockhash(block.number - 1),
                msg.sender,
                block.timestamp,
                address(this) // This will refer to the calling contract's address
            )
        );

        color_1[_tokenId] = bytes2(predictableRandom[0]) |
            (bytes2(predictableRandom[1]) >> 4) |
            (bytes3(predictableRandom[2]) >> 8);

        color_2[_tokenId] = bytes2(predictableRandom[3]) |
            (bytes2(predictableRandom[2]) >> 16) |
            (bytes3(predictableRandom[1]) >> 8);

        color_3[_tokenId] = bytes2(predictableRandom[2]) |
            (bytes2(predictableRandom[0]) >> 8) |
            (bytes3(predictableRandom[1]) >> 16);

        color_4[_tokenId] = bytes2(predictableRandom[4]) |
            (bytes2(predictableRandom[5]) >> 4) |
            (bytes3(predictableRandom[6]) >> 16);
    }
}","keccak256":"0xce04da0a9f9b8a5b2306b91b35cc76711591a8e70041e64c86611e5c5b843a99","license":"MIT"},"contracts/NBANFT/lib/NFTDescriptor.sol":{"content":"//SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

pragma abicoder v2;

import "@openzeppelin/contracts/utils/Strings.sol";
import 'base64-sol/base64.sol';
import "./ToColor.sol";
import "./TruncateAddress.sol";

/// @notice Helper to generate SVGs
library NFTDescriptor {
    using Strings for uint256;
    using Strings for uint160;
    using ToColor for bytes3;
    using TruncateAddress for string;

    struct SVGParams {
        uint256 chainId;
        uint256 tokenId;
        bytes3 color_1;
        bytes3 color_2;
        bytes3 color_3;
        bytes3 color_4;
        address implementation;
        address mainAccount;
        address owner;
    }

    // @dev generate SVG header
    function generateSVGHead() private pure returns (string memory) {
        return
            string(
                abi.encodePacked(
                    '<svg xmlns="http://www.w3.org/2000/svg" width="290" height="350" viewBox="0 0 290 350" xmlns:xlink="http://www.w3.org/1999/xlink">'
                )
            );
    }

    function generateSVGDef() private pure returns (string memory svg) {
        svg = string(
            abi.encodePacked(
                '<defs>',
                '<clipPath id="corners"><rect width="290" height="350" rx="42" ry="42"/>',
                '</clipPath></defs>'
            )
        );
    }

    function generateSVGBackground() private pure returns (string memory svg) {
        svg = string(
            abi.encodePacked(
                '<g id="background" clip-path="url(#corners)">',
                '<rect fill="1f9840" x="0px" y="0px" width="290px" height="350px"/>',
                '<rect style="filter: url(#f1)" x="0px" y="0px" width="290px" height="350px"/>',
                '<rect x="0" y="0" width="290" height="350" rx="42" ry="42" fill="rgba(0,0,0,0)" stroke="rgba(255,255,255,0.2)"/></g>'
            )
        );
    }

    function generateSVGInnerBorder() private pure returns (string memory svg) {
        svg = string(
            abi.encodePacked(
                '<g id="inner-border">',
                '<rect x="16" y="16" width="258" height="317" rx="26" ry="26" fill="rgba(0,0,0,0)" stroke="rgba(255,255,255,0.2)"/></g>'
            )
        );
    }

    function generateSVGCircle() private pure returns (string memory svg) {
        svg = string(
            abi.encodePacked(
                '<g id="circle">',
                '<circle cx="139px" cy="100px" r="51px" fill="none" stroke="white"/></g>'
            )
        );
    }

    function generateSVGInnerCircle_1(bytes3 _color) private pure returns (string memory svg) {
        svg = string(
            abi.encodePacked(
                '<g id="inner-circle-1">',
                '<circle cx="162px" cy="73px" r="8px" fill="#',
                _color.toColor(),
                '"/></g>'
            )
        );
    }

    function generateSVGInnerCircle_2(bytes3 _color) private pure returns (string memory svg) {
        svg = string(
            abi.encodePacked(
                '<g id="inner-circle-2">',
                '<circle cx="137px" cy="97px" r="15px" fill="#',
               _color.toColor(),
                '"/></g>'
            )
        );
    }

    function generateSVGInnerCircle_3(bytes3 _color) private pure returns (string memory svg) {
        svg = string(
            abi.encodePacked(
                '<g id="inner-circle-3">',
                '<circle cx="105px" cy="98px" r="9px" fill="#',
               _color.toColor(),
                '"/></g>'
            )
        );
    }

    function generateSVGInnerCircle_4(bytes3 _color) private pure returns (string memory svg) {
        svg = string(
            abi.encodePacked(
                '<g id="inner-circle-4">',
                '<circle cx="142px" cy="138px" r="12px" fill="#',
               _color.toColor(),
                '"/></g>'
            )
        );
    }

    function generateSVGID(uint256 _tokenId) private pure returns (string memory svg) {
        svg = string(
            abi.encodePacked(
                '<g id="Id-text" style="transform:translate(29px, 220px)">',
                '<rect width="85px" height="26px" rx="8px" ry="8px" fill="rgba(0,0,0,0.6)"/>',
                '<text x="12px" y="17px" font-family="',
                "'Courier New', monospace",
                '" font-size="20px" fill="white">',
                '<tspan fill="rgba(255,255,255,0.6)">ID: </tspan>',
                unicode'💎',
                _tokenId.toString(),
                '</text></g>'
            )
        );
    }

    function generateSVGMainAccount(address _account) private pure returns (string memory svg) {
        string memory _accountAddress = _account == address(0) ? "Not set" : uint160(_account).toHexString(20).truncateAddress();
        svg = string(
            abi.encodePacked(
                '<g id="account-text" style="transform:translate(29px, 255px)">',
                '<rect width="218px" height="26px" rx="8px" ry="8px" fill="rgba(0,0,0,0.6)"/>',
                '<text x="12px" y="17px" font-family="',
                "'Courier New', monospace",
                '" font-size="12px" fill="white">',
                '<tspan fill="rgba(255,255,255,0.6)">Main Acc',
                unicode'💼',
                ': </tspan>',
                _accountAddress,
                '</text></g>'
            )
        );
    }

    function generateSVGOwner(address _owner) internal  pure returns (string memory svg) {
        string memory _ownerAddress =uint160( _owner).toHexString(20);
        svg = string(
            abi.encodePacked(
                '<g id="owner-text" style="transform:translate(29px, 288px)">',
                '<rect width="218px" height="26px" rx="8px" ry="8px" fill="rgba(0,0,0,0.6)"/>',
                '<text x="12px" y="17px" font-family="',
                "'Courier New', monospace",
                '" font-size="12px" fill="white">',
                '<tspan fill="rgba(255,255,255,0.6)">Owner',
                unicode'🤖',
                ': </tspan>',
                _ownerAddress.truncateAddress(),
                '</text></g>'
            )
        );
    }

    function generateSVGImage(SVGParams memory _svgParams) internal pure returns (string memory svg) {
        return string(
            abi.encodePacked(
                generateSVGHead(),
                generateSVGDef(),
                generateSVGBackground(),
                generateSVGInnerBorder(),
                generateSVGCircle(),
                generateSVGInnerCircle_1(_svgParams.color_1),
                generateSVGInnerCircle_2(_svgParams.color_2),
                generateSVGInnerCircle_3(_svgParams.color_3),
                generateSVGInnerCircle_4(_svgParams.color_4),
                generateSVGID(_svgParams.tokenId),
                generateSVGMainAccount(_svgParams.mainAccount),
                generateSVGOwner(_svgParams.owner),
                "</svg>"
            )
        );
  }

    // @dev generate Json Metadata description
    function generateDescription(SVGParams memory params) internal pure returns (string memory) {
        return
            string(
                abi.encodePacked(
                    "This NFT has ID #",
                    params.tokenId.toString(),
                    " and is owned by ",
                    unicode"🤖",
                    uint160(params.owner).toHexString(20),
                    '\\n',
                    "NBA NFT is an experiment",
                    unicode"🧪",
                    " at combining digital ownership with self-governing capabilities"
                )
            );
    }

    /// @dev generate Json Metadata attributes
    function generateAttributes(SVGParams memory _svgParams) internal pure returns (string memory) {
        return
            string(
                abi.encodePacked(
                    "[",
                    getJsonAttribute("Chain ID", _svgParams.chainId.toString(), false),
                    getJsonAttribute("Default Implementation",uint160(_svgParams.implementation).toHexString(20), false),
                    abi.encodePacked(
                        getJsonAttribute("Main Account", uint160(_svgParams.mainAccount).toHexString(20), false),
                        getJsonAttribute("Owner", uint160(_svgParams.owner).toHexString(20), true),
                        "]"
                    )

                )
            );
    }

    /// @dev Get the json attribute as
    ///    {
    ///      "trait_type": "Skin",
    ///      "value": "Human"
    ///    }
    function getJsonAttribute(
        string memory trait,
        string memory value,
        bool end
    ) private pure returns (string memory json) {
        return string(abi.encodePacked('{ "trait_type" : "', trait, '", "value" : "', value, '" }', end ? "" : ","));
    }
}","keccak256":"0x61a08674e66fd020d8c663266fd139aba1f0ef7b3053ce3abbfb5e6c7e0118ec","license":"MIT"},"contracts/NBANFT/lib/ToColor.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

library ToColor {
    bytes16 internal constant ALPHABET = "0123456789abcdef";

    function toColor(bytes3 value) internal pure returns (string memory) {
        bytes memory buffer = new bytes(6);
        for (uint256 i = 0; i < 3; i++) {
            buffer[i * 2 + 1] = ALPHABET[uint8(value[i]) & 0xf];
            buffer[i * 2] = ALPHABET[uint8(value[i] >> 4) & 0xf];
        }
        return string(buffer);
    }
}
","keccak256":"0xe60a9d972912e69cbbdb6dfc5d777d37a2bf50a9000a1a7e140c1e2b077fa979","license":"MIT"},"contracts/NBANFT/lib/TruncateAddress.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

library TruncateAddress {

    function truncateAddress(string memory _address) internal pure returns(string memory) {
        bytes memory addressBytes = bytes(_address);
        require(addressBytes.length == 42, "Invalid address length");

        // Taking first 6 characters ('0x' + first 4 characters of the address) 
        // and the last 4 characters of the address.
        bytes memory result = new bytes(13);

        // Copying the first 6 characters
        for (uint i = 0; i < 6; i++) {
            result[i] = addressBytes[i];
        }

        // Adding the ellipsis
        result[6] = '.';
        result[7] = '.';
        result[8] = '.';

        // Copying the last 4 characters
        for (uint i = 0; i < 4; i++) {
            result[9 + i] = addressBytes[38 + i];
        }

        return string(result);
    }

}
